// Route based on action parameter
const action = $json.body?.action || $json.query?.action || 'intake';
return [{ json: { ...($json.body || $json), action: action } }];

const webhookData = $('Get Action').first().json;
const extractData = $input.first().json;

return [{
  json: {
    submission_id: webhookData.submission_id,
    researcher_id: webhookData.researcher_id,
    affiliation: webhookData.affiliation,
    fileType: webhookData.fileType,
    original_filename: webhookData.original_filename,
    sha256: extractData.sha256 || 'pending',
    extracted_text: extractData.extracted_text || 'No text extracted'
  }
}];

const baseUrl = 'https://natlab-glp-production.up.railway.app';

const prepData = $('Prepare Data').first().json;
const aiResponse = $input.first().json;

let rawText = '';
let review = {};

try {
  // Try multiple ways to get the AI response text
  if (typeof aiResponse === 'string') {
    rawText = aiResponse;
  } else if (aiResponse.message && aiResponse.message.content) {
    rawText = aiResponse.message.content;
  } else if (aiResponse.text) {
    rawText = aiResponse.text;
  } else if (aiResponse.content) {
    rawText = aiResponse.content;
  } else if (aiResponse.output) {
    rawText = aiResponse.output;
  } else {
    rawText = JSON.stringify(aiResponse);
  }

  // Clean up the text - remove markdown code blocks
  let cleanText = rawText;
  cleanText = cleanText.replace(/```json\s*/gi, '');
  cleanText = cleanText.replace(/```\s*/gi, '');
  cleanText = cleanText.trim();

  // Try to find JSON in the response
  const jsonMatch = cleanText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    review = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (e) {
  // Fallback review
  review = { 
    doc_type: 'UNKNOWN',
    decision: 'REVISION_NEEDED', 
    score: 50, 
    summary: 'AI analysis completed but parsing failed. Manual review required.', 
    issues: ['Automated parsing encountered an issue - please review manually'], 
    recommended_changes: ['Submit document for manual PI review'],
    missing_sections: [],
    alcoa_flags: {
      attributable: false,
      legible: false,
      contemporaneous: false,
      original: false,
      accurate: false,
      complete: false,
      consistent: false
    }
  };
}

// Ensure all required fields exist with new schema
review.doc_type = review.doc_type || 'UNKNOWN';
review.decision = review.decision || 'REVISION_NEEDED';
review.score = typeof review.score === 'number' ? review.score : 50;
review.summary = review.summary || 'Review completed';
review.issues = Array.isArray(review.issues) ? review.issues : [];
review.recommended_changes = Array.isArray(review.recommended_changes) ? review.recommended_changes : [];
review.missing_sections = Array.isArray(review.missing_sections) ? review.missing_sections : [];
review.alcoa_flags = review.alcoa_flags || {};

// Backwards compatibility
review.recommendations = review.recommended_changes;
review.compliant = review.decision === 'APPROVE';

// Simple token: base64 of submission_id + secret suffix
const tokenBase = String(prepData.submission_id) + '_glp_2024_sec';
const token = Buffer.from(tokenBase).toString('base64').replace(/[^a-zA-Z0-9]/g, '').substring(0, 32);

// Decision based on AI verdict
const isApproved = review.decision === 'APPROVE';

// Build ALCOA summary for DATA documents
let alcoaSummary = '';
if (review.doc_type === 'DATA' && review.alcoa_flags) {
  const flags = review.alcoa_flags;
  const passed = Object.values(flags).filter(v => v === true).length;
  const total = Object.keys(flags).length;
  alcoaSummary = `ALCOA: ${passed}/${total} criteria met`;

  const failed = Object.entries(flags)
    .filter(([k, v]) => v === false)
    .map(([k]) => k.charAt(0).toUpperCase() + k.slice(1));
  if (failed.length > 0) {
    alcoaSummary += ` (Failed: ${failed.join(', ')})`;
  }
}

return [{
  json: {
    submission_id: prepData.submission_id,
    researcher_id: prepData.researcher_id,
    affiliation: prepData.affiliation,
    fileType: prepData.fileType,
    original_filename: prepData.original_filename,
    sha256: prepData.sha256,
    extracted_text: prepData.extracted_text,
    review: review,
    doc_type: review.doc_type,
    decision: review.decision,
    is_approved: isApproved,
    is_compliant: isApproved,
    alcoa_summary: alcoaSummary,
    token: token,
    download_url: baseUrl + '/api/di/download/' + prepData.submission_id,
    approve_url: baseUrl + '/api/di/approve/' + prepData.submission_id + '?token=' + token,
    revise_url: baseUrl + '/api/di/revise/' + prepData.submission_id + '?token=' + token,
    sign_url: baseUrl + '/api/di/sign/' + prepData.submission_id + '?token=' + token
  }
}];
